lib/sendgrid.ts.content:
import { MailService } from '@sendgrid/mail';

if (!process.env.SENDGRID_API_KEY) {
  throw new Error("SENDGRID_API_KEY environment variable must be set");
}

const mailService = new MailService();
mailService.setApiKey(process.env.SENDGRID_API_KEY);

interface ContactEmailParams {
  name: string;
  email: string;
  message: string;
}

export async function sendContactEmail(params: ContactEmailParams): Promise<boolean> {
  try {
    await mailService.send({
      to: 'info@techeduvate.com',
      from: 'noreply@techeduvate.com', // This should be a verified domain in SendGrid
      subject: `New Contact Form Submission from ${params.name}`,
      text: `
Name: ${params.name}
Email: ${params.email}
Message: ${params.message}
      `,
      html: `
        <h3>New Contact Form Submission</h3>
        <p><strong>Name:</strong> ${params.name}</p>
        <p><strong>Email:</strong> ${params.email}</p>
        <p><strong>Message:</strong></p>
        <p>${params.message.replace(/\n/g, '<br>')}</p>
      `,
      replyTo: params.email
    });
    return true;
  } catch (error) {
    console.error('SendGrid email error:', error);
    return false;
  }
}

